targets.expr:
avg((time() - container_start_time_seconds{job=~"k8s-cadvisor-$cluster", namespace=~"$namespace", pod=~"$app-$group.*", image!="registry.k8s.io/pause:3.9", container=~"$container", container!~"POD|cronjob|"}) 
* on(pod, namespace) group_left(label_app)
kube_pod_labels{namespace=~"$namespace", pod=~"$app-$group.*"}
) by (namespace, label_app, container) < 3600

avg((
  sum(container_memory_working_set_bytes{
    job="k8s-cadvisor-$cluster",
    pod=~"$app-$group.*",
    container=~"$container", 
    container!~"POD", 
    namespace=~"$namespace"
  }) by (pod, container)
  /
  sum(kube_pod_container_resource_limits{
    namespace=~"$namespace",
    pod=~"$app-$group.*",
    resource="memory"
  }) by (pod, container)
) * 100
* on(pod) group_left(label_app)
kube_pod_labels{namespace=~"$namespace", pod=~"$app-$group.*", label_app!~""}
) by (label_app, container)

avg((sum(rate(container_cpu_usage_seconds_total{job=~"k8s-cadvisor-$cluster", pod=~".*$group.*", pod=~"$app.*", namespace=~"$namespace", image!="registry.k8s.io/pause:3.9", container=~"$container", container!~"^(POD|)$"}[1m])) by (pod, container)
/ 
sum(kube_pod_container_resource_limits{namespace=~"$namespace", pod=~".*$group.*", pod=~"$app.*", resource="cpu"}) by (pod, container)) 
* on(pod) group_left(label_app, label_job_name)
kube_pod_labels{namespace=~"$namespace", pod=~".*$group.*", pod=~"$app.*", label_app!~""}
) by (label_app, label_job_name, container)

avg((container_memory_working_set_bytes{job=~"k8s-cadvisor-$cluster", kubernetes_io_hostname=~"$host", namespace=~"$namespace", pod=~"$app-$group.*", container!~"POD|", container=~"$container"}) 
* on(pod, namespace) group_left(label_app)
kube_pod_labels{namespace=~"$namespace", pod=~"$app-$group.*", label_app!~""}
) by (namespace, label_app, container)

avg((kube_pod_container_resource_requests{namespace=~"$namespace", pod=~"$app-$group.*", resource="memory", container=~"$container"}) 
